import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';


@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {
  List: any[] = [];


  public isLoading = false

  constructor(private http:HttpClient,private router: Router) { 

  }

  ngOnInit() {
    this.fetchUsers();

  }

  fetchUsers() {
    this.isLoading = true
    this.http.get<any[]>('http://localhost:3000/alllist').subscribe(
      lists => {
        this.List = lists;

        this.isLoading = false
      },
      error => {
        console.error(error);
      }
    );
  }

  redirectToBooking(lists: any) {
    this.router.navigate(['/book'], { queryParams: { hotelname: lists.HotelName } });
  }

}
<div class="flex">


<section class="popular" id="popular">


    <div class="box-container">
        <div class="box" *ngFor="let lists of List">
          <div class="col card p-4">
            <div >
              <img src="{{ lists.image }}" alt="{{ lists.HotelName }}" class="img-fluid mb-2 mb-md-0">
            </div>


                <div >
                    <h3>{{ lists.HotelName }}</h3>
                    <p>{{ lists.Address }},{{ lists.City }},{{ lists.Postalcode }}</p>

                    <p>rating: {{ lists.rating }}</p>   
                </div>

                <div class="buttons-container"><button class="btn btn-primary " (click)="redirectToBooking(lists)">Book</button></div>
            </div>
        </div>        
    </div>
</section>
</div>
