import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-admin-register',
  templateUrl: './admin-register.component.html',
  styleUrls: ['./admin-register.component.css']
})
export class AdminRegisterComponent {
  user = {
    HotelName: "",
    password: ""
  };

  constructor(private http: HttpClient, private route: Router) {}

  submitForm(): void {
    this.http.post('http://localhost:3000/admin/register', this.user)
      .subscribe(
        (res: any) => {
          if (res.message === 'Successfully Registered') {
            window.alert('Registration Successful!');
            this.route.navigate(['/admin/login']);
          } else {
            window.alert('Registration Failed!');
          }
        },
        (error) => {
          console.error(error);
          window.alert('Registration Failed!');
        }
      );
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta http-equiv="X-UA-Compatible" content="IE=edge">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <link href='https://unpkg.com/boxicons@2.1.4/css/boxicons.min.css' rel='stylesheet'>

    <title>Ludiflex | Login</title>
</head>
<body>
<div class="cc">
   <div class="box">
    <div class="container">
        <div class="top">
            <span>Don't Have an account?</span>
            <header> Admin Register</header>
        </div>
        <form #userForm="ngForm"   (ngSubmit)="submitForm()" >
        <div class="input-field">

            <input type="text" class="input" [(ngModel)]="user.HotelName"  placeholder="HotelName" name="Hotelname" id="Hotelname"  >

        </div>
        <br>
        <div class="input-field">
            <input type="password" class="input" [(ngModel)]="user.password" placeholder="Password" name="password" id="password" >

        </div>
        <br>


        <div class="input-field">
            <input type="submit" class="submit" value="Register" id="">


        </div>
        <div class="mt-2"><a routerLink="/admin/login" class="mt-2">Already have an account? click here</a> 

        </div>

    </form>
        <br>


    </div>
</div>
</div>  
</body>
</html>
