import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-admin-bookings',
  templateUrl: './admin-bookings.component.html',
  styleUrls: ['./admin-bookings.component.css']
})
export class AdminBookingsComponent implements OnInit {
  List: any[] = [];

  public isLoading = false;

  hotelName!: string | null;

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.hotelName = localStorage.getItem('HotelName');
    if (!this.hotelName) {
      console.error('Hotel name not found in local storage');
      return;
    }
    this.fetchBookings();
  }

  fetchBookings() {
    this.isLoading = true;


    this.http.get<any[]>(`http://localhost:3000/adminbookings/${this.hotelName}`).subscribe(
      lists => {

        this.List = lists;

        this.isLoading = false;
      },
      error => {
        console.error(error);
        this.isLoading = false;
      }
    );
  }

}

<!DOCTYPE html>
<html>
<style>
table, th, td {
  border:1px solid black;
}
th, td {
  text-align: center;
}
</style>
<body>
  <div class="d-flex">
   <app-admin-dash2></app-admin-dash2>
<div class="container pt-2 w-100" *ngIf="!isLoading">
    <h1 style="color: #104bb7; font-size: 38px; font-weight: bold; text-align: center;" class="mb-4">All Bookings</h1>
    <table style="width: 100%;">
      <tr>
        <th>UserID</th>
        <th>Booking ID</th>
        <th>Hotel Name</th>
        <th>Name</th>
        <th>Email</th>
        <th>Date</th>
        <th>Time</th>
        <th>Number of Guests</th>
      </tr>
      <ng-container *ngFor="let booking of List">
        <tr>
          <td>{{booking.userId}}</td>
          <td>{{booking._id}}</td>
          <td>{{booking.HotelName}}</td>
          <td>{{booking.name}}</td>
          <td>{{booking.email}}</td>
          <td>{{booking.date}}</td>
          <td>{{booking.time}}</td>
          <td>{{booking.guests}}</td>
        </tr>
      </ng-container>
    </table>
</div>
  </div>
</body>
</html>
